//能发送异步请求的ajax模块
//函数的返回值是promise对象
import axios from 'axios'
export default function ajax(url,data={},type='GET'){
   if(type==='GET'){              //发送GET请求
       return axios.get(url,{     //配置对象
           params:data            //指定请求参数
       })
   }else{                         //发送POST请求
      return axios.post(url,data)
   }
}
