import React, { Component } from 'react'
import {Card,Select,Input,Button,Icon,Table} from 'antd'
import {getProducts} from '../../api/index'
import LinkButton from '../../components/link-button'
import PAGE_SIZE from '../../utils/constants'
//Product的默认子路由组件
const Option=Select.Option
export default class ProductHome extends Component {
    state={
        products:[ ], //商品的数组
        total:0,  //总的数据条数
    }
    //初始化列的方法
    initColumns=()=>{
        return this.columns=[
            {
              title: '商品名称',
              dataIndex: 'name',
            },
            {
              title: '商品描述',
              dataIndex: 'desc',
            },
            {
              title: '价格',
              render:(product)=>
                    '￥'+ product.price    //当前指定了对应的属性 
            },
            {
                width:100,
                title: '状态',
                render:(product)=>{
                    return (
                        <span>
                            <Button type='primary'>下架</Button>
                            <span>在售</span>
                        </span>
                    )
                }
            },
            {
                width:100,
                title: '操作',
                render:(product)=>{
                    return (
                        <span>
                            <LinkButton>详情</LinkButton>
                            <LinkButton>修改</LinkButton>
                        </span>
                    )
                }
            }
          ]
    }
    //获取商品的方法
    getProducts=async(pageNum)=>{
       const result= await getProducts(pageNum,3)
        if(result.status===0){
            const {total,list}=result.data
            this.setState({total,products:list})
        }
    }
    componentWillMount(){
        this.initColumns()  //调用初始化列的方法
    }
    componentDidMount(){
        this.getProducts(1)   //调用获取商品的方法
    }
    render() {
        //取出状态数据
        const {products}=this.state
       
        const title=(
            <span>
                <Select value='1' style={{width:150}}>
                    <Option value='1'>按名称搜索</Option>
                    <Option value='2'>按描述搜索</Option>
                </Select>
                <Input placeholder='关键字' style={{width:150,margin:'0 15px'}}></Input>
                <Button type='primary'>搜索</Button>
            </span>
        )
        const extra=(
           <Button type='primary'>
                <Icon type='plus' ></Icon>   添加商品
           </Button>
        )
        return (
          <Card title={title} extra={extra}>
              <Table dataSource={products} columns={this.columns} rowKey='_id' bordered
              pagination={{defaultPageSize:PAGE_SIZE}}
              ></Table>
          </Card>
        )
    }
}
