import React, { Component } from 'react'
import { Card, Button, Table ,Modal} from 'antd'
import { reqRoles,reqAddRole} from '../../api'
import AddForm from './add-form'
export default class Role extends Component {
    state = {
        roles: [],    //所有角色的数组
        role:{},  //选中的role 
        isShowAdd:false  //标识确认框的显示 隐藏
    }
    //列的数组  初始化列的数组
    initColumn = () => {
        this.columns = [
            {
                title: '角色名称',
                dataIndex: 'name',
            },
            {
                title: '创建时间',
                dataIndex: 'create_time',
            },
            {
                title: '授权时间',
                dataIndex: 'auth_time',
            },
            {
                title: '授权人',
                dataIndex: 'auth_name',
            }
        ]
    }
    //添加角色 添加角色的确认按钮
    addRole=async()=>{
        //收集输入数据
        console.log(this.form)
        //请求添加
        const result=await reqAddRole()
        //根据结果提示信息
        this.setState({
            isShowAdd:false
        })
    }
   
    //获取角色列表的方法
    getRoles=async()=>{
         const result=await reqRoles()
         if(result.status===0){
             const roles=result.data
             this.setState({
                 roles
             })
         }
    }
    componentWillMount(){
        this.initColumn()
    }
    componentDidMount(){
        this.getRoles()   //获取所有角色列表
    }
    onRow = (role) => {  //role是点击当前行的的角色
        return {
          onClick: event => { // 点击行
            console.log('row onClick()', role)
            this.setState({
              role   //将选中的角色存起来
            })
          },
        }
      }
    render() {
        const { roles,role ,isShowAdd} = this.state
        const title = (
            <span>
                <Button type='primary' onClick={()=>{this.setState({isShowAdd:true})}}>创建角色</Button>&nbsp;
                <Button type='primary' disabled={!role._id}>设置角色权限</Button>
            </span>
        )
        return (
            <Card title={title}>
                <Table
                    bordered
                    rowKey='_id'
                    pagination={{ defaultPageSize: 5, showQuickJumper: true }}
                    dataSource={roles}   //每一行的数据
                    columns={this.columns} //每一列的数据
                    rowSelection={{   //选择功能的配置
                        selectedRowKeys:[role._id],  //指定选中项的 key 数组，需要和 onChange 进行配合
                        type:'radio'   //配置选择项是单选还是多选
                    }}
                    onRow={this.onRow}  //点击每一行的事件，可以看出是点击的哪一行
                    >   
                </Table>
                <Modal
                        title="添加角色"
                        visible={isShowAdd}
                        //添加分类
                        onOk={this.addRole}
                        onCancel={()=>{this.setState({isShowAdd:false})}}
                    >
                        {/* 把接收的form存到父组件里面 */}
                        <AddForm setForm={(form)=>{this.form=form}}></AddForm>  
                    </Modal>
            </Card>
        )
    }
}
