import React, { Component } from 'react'
import { Card, Table, Button, Icon } from 'antd';
import './index.less'
import LinkButton from '../../components/link-button'
import {reqCategorys} from '../../api/index'
export default class Category extends Component {
    state={
        categorys:[],   //一级分类列表
        columns:[]    //每一列的内容
    }
    //初始化所有Table所有列的数组
    componentWillMount(){
        this.initColumns()   //初始化所有列的数组方法
        // this.getCategorysData() //初始化数据
    }

    //初始化所有商品的数据
     getCategorysData=async()=>{
        const res=await reqCategorys()
        this.setState({categorys:res})
        console.log(res)
    }
    //初始化列的方法
    initColumns=()=>{
       this.columns = [
            {
                title: '分类的名称',  
                dataIndex: 'name', //显示数据的对应的属性名
                key: 'name',
            },
            {
                title: '操作', 
                width:300,
                dataIndex: '',  //显示数据的对应的属性名
                key: 'x',
                render: () => (  //返回需要指定的界面标签
                    <span>
                        <LinkButton onClick={this.a}>修改分类</LinkButton>
                        <LinkButton onClick={this.b}>查看子分类</LinkButton>
                    </span>
                )
            },
        ]
    }
    render() {
        //读取状态数据
        const  {categorys,columns}=this.state
        //card的左侧
        const title = '一级分类列表'
        //card的右侧
        const extra = (
            <Button type="primary">
                <Icon type='plus'></Icon>
                添加
            </Button>
        )
 

        return (
            <div>
                <Card title={title} extra={extra} >
                    <Table dataSource={categorys} columns={columns}   bordered rowKey='_id'/>
                </Card>
            </div>
        )
    }
}
