import React, { Component } from 'react'
import {Card,Select,Input,Button,Icon,Table} from 'antd'
import {getProducts,reqSearchProducts} from '../../api/index'
import LinkButton from '../../components/link-button'
import {PAGE_SIZE} from '../../utils/constants'
//Product的默认子路由组件
const Option=Select.Option
export default class ProductHome extends Component {
    state={
        products:[ ], //商品的数组
        total:0,  //总的数据条数
        loading:false,    //状态
        searchName:'',   //搜索的关键字
        searchType:'productName'    //根据哪个字段来搜索
    }
    //初始化列的方法
    initColumns=()=>{
        return this.columns=[
            {
              title: '商品名称',
              dataIndex: 'name',
            },
            {
              title: '商品描述',
              dataIndex: 'desc',
            },
            {
              title: '价格',
              render:(product)=>
                    '￥'+ product.price    //当前指定了对应的属性 
            },
            {
                width:100,
                title: '状态',
                render:(product)=>{
                    return (
                        <span>
                            <Button type='primary'>下架</Button>
                            <span>在售</span>
                        </span>
                    )
                }
            },
            {
                width:100,
                title: '操作',
                render:(product)=>{
                    return (
                        <span>
                            <LinkButton>详情</LinkButton>
                            <LinkButton>修改</LinkButton>
                        </span>
                    )
                }
            }
          ]
    }
    //搜索商品的方法
    serachProduct=()=>{
        console.log(this.state.searchName,this.state.searchType)
    }
    //获取商品的方法
    getProducts=async(pageNum)=>{
        this.setState({loading:true})  //显示loading
       const result= await getProducts(pageNum,PAGE_SIZE)
       this.setState({loading:false})  //请求结束之后 隐藏loading
        if(result.status===0){
            const {total,list}=result.data
            this.setState({total,products:list})
        }
    }
    componentWillMount(){
        this.initColumns()  //调用初始化列的方法
    }
    componentDidMount(){
        this.getProducts(1)   //调用获取商品的方法
    }
    render() {
        //取出状态数据
        const {products,total,loading,searchName,searchType}=this.state
       
        const title=(
            <span>
                <Select value={searchType} style={{width:150}} onChange={value=>this.setState({searchType:value})}>
                    <Option value='productName'>按名称搜索</Option>
                    <Option value='productDesc'>按描述搜索</Option>
                </Select>
                <Input placeholder='关键字' style={{width:150,margin:'0 15px'}} value={searchName} onChange={(e)=>{this.setState({searchName:e.target.value})}}></Input>
                <Button type='primary' onClick={this.serachProduct}>搜索</Button>
            </span>
        )
        const extra=(
           <Button type='primary'>
                <Icon type='plus' ></Icon>   添加商品
           </Button>
        )
        return (
          <Card title={title} extra={extra}>
              <Table dataSource={products}
               columns={this.columns} 
               rowKey='_id'
                bordered
                loading={loading}
              pagination={{defaultPageSize:PAGE_SIZE,
                showQuickJumper:true,
                total,
                onChange:(pageNum)=>{this.getProducts(pageNum)},
            }}
              ></Table>
          </Card>
        )
    }
}
