import React, { Component } from 'react'
import {Card,Icon,Input,Form,Cascader,Upload,Button} from 'antd'
import LinkButton from '../../components/link-button';
const Item=Form.Item
const { TextArea } = Input;  //多行输入框
//product的添加和更新的子路由组件
export default class ProductAddUpdate extends Component {
    render() {
        const title=(
            <span>
                <LinkButton>
                   <Icon type='arrow-left' style={{fontSize:20}}></Icon>
                </LinkButton>
                <span>添加商品</span>
            </span>
        )
        //指定Item布局的配置对象
        const formItemLayOut={
            labelCol:{span:2},   //左侧label的宽度
            wrapperCol:{span:8}   //指定右侧包裹的宽度
        }
        //输入框最后面的后缀
        const selectAfter = (
           <span>元</span>
          );
        return (
           <Card
            title={title}
           >
               <Form {...formItemLayOut}>
                   <Item label="商品名称">
                       <Input placeholder='请输入商品名称'></Input>
                   </Item>
                   <Item label="商品描述">
                       <TextArea placeholder='请输入商品描述' autosize={{minRows:2,maxRows:6}} ></TextArea>
                   </Item>
                   <Item label="商品价格">
                       <Input type='number' placeholder='请输入商品价格' addonAfter={selectAfter}></Input>
                   </Item>
                   <Item label="商品分类">
                      <div>商品分类</div>
                   </Item>
                   <Item label="商品图片">
                     <div>商品图片</div>
                   </Item>
                   <Item label="商品详情">
                     <div>商品详情</div>
                   </Item>
                   <Item label="提交">
                        <Button type='primary'>提交</Button>
                   </Item>
               </Form>
           </Card>
        )
    }
}
