import React, { Component } from 'react'
import './index.less'
import logo from '../../assets/images/n.jpg'
import {Link,withRouter} from 'react-router-dom'
import { Menu, Icon} from 'antd';
import menuList from '../../config/menuConfig.js'
const SubMenu = Menu.SubMenu;
 class LeftNav extends Component {

    //根据menu数组生成对应的标签数组   map+递归调用
  /*   getMenuNodes=(menuList)=>{
        return menuList.map((item)=>{
                //    {
                //         title:'商品',  菜单标题名称
                //         key:'/products',
                //         icon:'appstore',
                //         children:[       //子菜单列表  可能有 可能没有
                //   } 
               if(!item.children){
                   return (
                          <Menu.Item key={item.key}>
                            <Link to={item.key}>
                            <Icon type={item.icon} />
                            <span>{item.title}</span>
                            </Link>
                        </Menu.Item>
                   )
               }else{
                   return (
                    <SubMenu
                    key={item.key}
                    title={
                      <span>
                        <Icon type={item.icon}/>
                        <span>{item.title}</span>
                      </span>
                    }
                  >
                  {
                      this.getMenuNodes(item.children)
                  }
                  </SubMenu> 
                   )
               }
            }
        )
    } */
     //根据menu数组生成对应的标签数组   reduce+递归调用
    getMenuNodes=((menuList)=>{
        return menuList.reduce(((pre,item)=>{
            //向pre 添加<Menu.Item/>
            //向pre 添加<SubMneu />
            if(!item.children){
                pre.push(( <Menu.Item key={item.key}>
                    <Link to={item.key}>
                    <Icon type={item.icon} />
                    <span>{item.title}</span>
                    </Link>
                </Menu.Item>))
            }else{
                pre.push((
                    <SubMenu
                    key={item.key}
                    title={
                      <span>
                        <Icon type={item.icon}/>
                        <span>{item.title}</span>
                      </span>
                    }
                  >
                  {
                      this.getMenuNodes(item.children)
                  }
                  </SubMenu> 
                ))
            }
                return pre
        }),[])
    })
    render() {
            //得到当前请求的路由路径  但是left-nav不是路由组件 没有location属性  用withRouter将一般组件拥有路由组件的属性
       const path=this.props.location.pathname
        return (
           <div>
             <header className="left-nav">
                <Link  to='/' className='left-nav-header'>
                    <img alt='logo' src={logo}></img>
                    <h1>商品后台</h1>
                </Link>
            </header>

            <Menu
          defaultSelectedKeys={[path]}
          defaultOpenKeys={['']}  //默认选中那个菜单项
          mode="inline"
          theme="dark" >
            {
                this.getMenuNodes(menuList)
            }
        </Menu>
           </div>
        )
    }
}
//withRouter高阶组件
//包装非路由组件 产生一个新的路由组件
//新的组件向非路由组件传递3个属性：history/location/match
export default withRouter(LeftNav)   //将一般组件拥有路由组件的属性
