import React, { Component } from 'react'
import { Input, Form, Tree } from 'antd'
import PropTypes from 'prop-types'
import menuList from '../../config/menuConfig'
const Item = Form.Item
const { TreeNode } = Tree;
//添加分类的form组件
export default class AuthForm extends Component {
    static propTypes = {
        role: PropTypes.object,   //从父组件接收到的role
    }
    //定义生成树形结构的方法
    getTreeNodes=(menuList)=>{
     return menuList.reduce((pre,item)=>{
          pre.push(
            <TreeNode title={item.title} key={item.key} >
                {/* 如果当前的节点有子节点，就继续递归 */}
                {item.children?this.getTreeNodes(item.children):null}  
            </TreeNode>
          )
          return pre
      },[])
    }
    componentWillMount(){  //调用生成树形结构的方法
        this.treeNodes=this.getTreeNodes(menuList)
    }
    render() {
        const formItemLatout = {
            labelCol: { span: 4 },  //左侧label的宽度
            wrapperCol: { span: 15 } //右侧包裹的宽度
        }
        const { role } = this.props
        return (
            <Form>
                <Item label='角色名称' {...formItemLatout}>
                    <Input value={role.name} disabled></Input>
                </Item>
                <Tree
                    checkable
                    defaultExpandAll={true}
                    checkedKeys={role.menus}   //当前点击行的role的权限
                >
                    <TreeNode title="平台权限" key="all">
                        {this.treeNodes}
                        {/* <TreeNode title="parent 1-0" key="0-0-0">
                            <TreeNode title="leaf" key="0-0-0-0"  />
                            <TreeNode title="leaf" key="0-0-0-1" />
                        </TreeNode>
                        <TreeNode title="parent 1-1" key="0-0-1">
                            <TreeNode  key="0-0-1-0" />
                        </TreeNode> */}
                    </TreeNode>
                </Tree>
            </Form>
        )
    }
}
