import React, { Component } from 'react'
import { Card, Icon, List } from 'antd'
import LinkButton from '../../components/link-button'
import {BASE_IMG_URL} from '../../utils/constants'
import {reqCategory} from '../../api/index'
const Item = List.Item
//product的详情的子路由组件
export default class ProductDetail extends Component {
    state={
        cName1:'', //一级分类名称
        cName2:''  //二级分类名称
    }
  async  componentDidMount(){
        //得到当前商品的父id 和自己id
        const {pCategoryId,categoryId}=this.props.location.state.product
        if(pCategoryId==='0'){ //一级分类下的商品
            const result=  await reqCategory(categoryId)
            const cName1=result.data.name
            this.setState({cName1})
        }  else{  //二级分类下的商品
            const cName1=await reqCategory(pCategoryId)
            const cName2=await reqCategory(categoryId)
            this.setState({cName1,cName2})
        }
       
    }
    render() {
        const title = (
            <span>
                <LinkButton>
                    <Icon type='arrow-left' style={{ marginRight: 10, fontSize: 20 }}
                        onClick={() => { this.props.history.goBack() }}></Icon>
                </LinkButton>
                <span type='primary'>商品详情</span>
            </span>
        )
        //读取传递过来的state数据
        const { name, desc, price, detail, imgs,cName1 ,cName2} = this.props.location.state.product
        return (
            <Card title={title} className='product-detail'>
                <List>
                    <Item>
                        <span className='left'>商品名称：</span>
                        <span>{name}</span>
                    </Item>
                    <Item>
                        <span className='left'>商品描述：</span>
                        <span>{desc} </span>
                    </Item>
                    <Item>
                        <span className='left'>商品价格：</span>
                        <span>{price}</span>
                    </Item>
                    <Item>
                        <span className='left'>所属分类：</span>
                        <span>{cName1}——{cName2} </span>
                    </Item>
                    <Item>
                        <span className='left'>商品图片：</span>
                        <span>
                            {
                                imgs.map(item => (
                                    <img
                                        key={item}
                                        src={BASE_IMG_URL + item}
                                        className="product-img"
                                        alt="img"
                                    />
                                ))
                            }
                        </span>
                    </Item>
                    <Item>
                        <span className='left'>商品详情：</span>
                        <span dangerouslySetInnerHTML={{ __html: detail }}>
                        </span>
                    </Item>
                </List>
            </Card>
        )
    }
}
