import React, { Component } from 'react'
import { Card, Icon, Input, Form, Cascader, Upload, Button, message } from 'antd'
import LinkButton from '../../components/link-button';
import { reqCategory, reqCategorys } from '../../api/index'
const Item = Form.Item
const { TextArea } = Input;  //多行输入框
//product的添加和更新的子路由组件
class ProductAddUpdate extends Component {
    state = {
        options: [],
    }

    //处理一级分类的数组
    initOptions = (categorys) => {
        //根据categorys数组生成options数组
        const options = categorys.map((c) => {
            return {
                value: c._id,
                label: c.name,
                isLeaf: false  //不是叶子
            }
        })
        this.setState({ options })
    }
    //获取一级分类列表 或者二级分类列表
    getCategorys = async (parentId) => {
        const result = await reqCategorys(parentId)
        if (result.status === 0) {
            const categorys = result.data
            //如果一级分类列表
            if (parentId === 0) {
                this.initOptions(categorys)   //利用一级分类数组 调用生成options的数组
            } else {  //二级分类列表
                return categorys   //返回二级列表  当前async函数返回的promise就会成功且value为categorys
            }
        }
    }
    //用于加载下一级列表数据的回调
    loadData = async selectedOptions => {
        //得到选择的option对象
        const targetOption = selectedOptions[0];
        // 显示loading效果
        targetOption.loading = true;

        //根据选中的分类，请求获取二级分类列表subCategorys
        const subCategorys = await this.getCategorys(targetOption.value)   //value是一级分类的id
        if (subCategorys && subCategorys.length > 0) {  //当前选中的分类有二级分类
         cOptions=subCategorys.map((c)=>{
                return {
                    value: c._id,
                    label: c.name,
                    isLeaf: true  //是叶子
                }
            })
        } else {  //当前选中的分类没有二级分类
            targetOption.isLeaf=true
        }
        this.setState({
            options: [...this.state.options],
        });
    };
    //验证价格 自定义验证函数
    validatorPrice = (rule, value, callback) => {   //value是字符串类型
        // callback()  //验证通过
        // callback('xxxx')   //验证没通过  并且指定错误信息
        if (value * 1 > 0) {
            callback() //验证通过
        } else {
            callback('价格必须大于0')
        }
    }
    //提交按钮
    submit = () => {
        //进行整体表单验证，整体通过才提交
        this.props.form.validateFields((err, value) => {
            if (!err) {
                message.success('添加成功')
            }
        })
    }
    componentDidMount() {
        this.getCategorys('0')
    }
    render() {
        const title = (
            <span>
                <LinkButton>
                    <Icon type='arrow-left' style={{ fontSize: 20 }}></Icon>
                </LinkButton>
                <span>添加商品</span>
            </span>
        )
        //指定Item布局的配置对象
        const formItemLayOut = {
            labelCol: { span: 2 },   //左侧label的宽度
            wrapperCol: { span: 8 }   //指定右侧包裹的宽度
        }
        //输入框最后面的后缀
        const selectAfter = (
            <span>元</span>
        );

        //获取表单验证
        const { getFieldDecorator } = this.props.form
        return (
            <Card
                title={title}
            >
                <Form {...formItemLayOut}>
                    <Item label="商品名称">
                        {
                            getFieldDecorator('name', {
                                initialValue: '',
                                rules: [
                                    { required: true, message: '必须输入商品名称' }
                                ]
                            })(<Input placeholder='请输入商品名称'></Input>)
                        }
                    </Item>
                    <Item label="商品描述">
                        {
                            getFieldDecorator('desc', {
                                initialValue: '',
                                rules: [
                                    { required: true, message: '必须输入商品描述' }
                                ]
                            })(<TextArea placeholder='请输入商品描述' autosize={{ minRows: 2, maxRows: 6 }} ></TextArea>)
                        }
                    </Item>
                    <Item label="商品价格">
                        {
                            getFieldDecorator('price', {
                                initialValue: '',
                                rules: [
                                    { required: true, message: '必须输入商品价格' },
                                    { validator: this.validatorPrice }
                                ]
                            })(<Input type='number' placeholder='请输入商品价格' addonAfter={selectAfter}></Input>)
                        }
                    </Item>
                    <Item label="商品分类">
                        <Cascader
                            options={this.state.options}    //需要显示的列表数据
                            loadData={this.loadData}        //指定当选择某个列表项，加载下一级列表的监听回调
                        />
                    </Item>
                    <Item label="商品图片">
                        <div>商品图片</div>
                    </Item>
                    <Item label="商品详情">
                        <div>商品详情</div>
                    </Item>
                    <Item >
                        <Button type='primary' onClick={this.submit}>提交</Button>
                    </Item>
                </Form>
            </Card>
        )
    }
}
export default Form.create()(ProductAddUpdate)
