import React, { Component } from 'react'
import { Card, Button, Table } from 'antd'
import { reqRoles } from '../../api'
export default class Role extends Component {
    state = {
        roles: [],    //角色的数组
    }
    //列的数组  初始化列的数组
    initColumn = () => {
        this.columns = [
            {
                title: '角色名称',
                dataIndex: 'name',
            },
            {
                title: '创建时间',
                dataIndex: 'create_time',
            },
            {
                title: '授权时间',
                dataIndex: 'auth_time',
            },
            {
                title: '授权人',
                dataIndex: 'auth_name',
            }
        ]
    }
    //获取角色列表的方法
    getRoles=async()=>{
         const result=await reqRoles()
         if(result.status===0){
             const roles=result.data
             this.setState({
                 roles
             })
         }
    }
    componentWillMount(){
        this.initColumn()
    }
    componentDidMount(){
        this.getRoles()   //获取所有角色列表
    }
    onRow = (role) => {  //role是点击当前行的的角色
        return {
          onClick: event => { // 点击行
            console.log('row onClick()', role)
            // alert('点击行')
            // this.setState({
            //   role
            // })
          },
        }
      }
    render() {
        const { roles } = this.state
        const title = (
            <span>
                <Button type='primary'>创建角色</Button>&nbsp;
                <Button type='primary' disabled>设置角色权限</Button>
            </span>
        )
        // const rowSelection={
        //     onChange: (selectedRowKeys, selectedRows) => {
        //         console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
        //       },
        //       getCheckboxProps: record => ({
        //         disabled: record.name === 'Disabled User', // Column configuration not to be checked
        //         name: record.name,
        //       }),
        // }
        return (
            <Card title={title}>
                <Table
                    bordered
                    pagination={{ defaultPageSize: 5, showQuickJumper: true }}
                    dataSource={roles}   //每一行的数据
                    columns={this.columns} //每一列的数据
                    rowSelection={{   //选择功能的配置
                        // rowSelection,
                        type:'radio'   //配置选择项是单选还是多选
                    }}
                    onRow={this.onRow}  //点击每一行的事件，可以看出是点击的哪一行
                    >   
                </Table>
            </Card>
        )
    }
}
