//用来根据现有的state和指定的action，返回新的state的函数
import {combineReducers}  from 'redux'
import storageUtils from "../utils/storageUtils"
import { SET_HEAD_TITLE } from './action-types'

const initHeadState='首页'
//用来管理头部标题的reducer函数
function  headTitle(state=initHeadState,action){
    switch(action.type){
        case SET_HEAD_TITLE:
        return action.data
        default:
            return state
    }
}


const initUser=storageUtils.getUser()
//用来管理当前登录用户的
function user(state=initUser,action){
    const {type,data}=action
    switch(type){
        default:
            return state
    }
}

//向外暴露合并之后的reducer  管理的总的state的结构：
//   {
//      headTitle:'首页'
//      user:storageUtils.getUser()
//   }
export default combineReducers({
    headTitle,user
})
