import React, { Component } from 'react'
import{
    Card,Button,Table,Modal
} from 'antd'
import formateDate from '../../utils/dateUtils'
import {reqUsers} from '../../api/index'
import LinkButton from '../../components/link-button'
export default class User extends Component {
    state={
        isShow:false,  //对话框的显示隐藏
        users:[],  //所有用户的数组
    }
    //获取所有用户的方法
    getUsers=async()=>{
        const result=await reqUsers()
        const newUsers=result.data.users
        this.setState({user:newUsers})
       console.log(result)
    }
    //初始化列的方法
    initColumns=()=>{
        this.columns=[
            {
                title:'用户名',
                dataIndex:'username',
            },
            {
                title:'邮箱',
                dataIndex:'email'
            },
            {
                title:'电话',
                dataIndex:'phone'
            },
            {
                title:'注册时间',
                dataIndex:'create_time',
                render:formateDate
            },
            {
                title:'所属角色',
                dataIndex:'_id',
            },
            {
                title:'操作',
                render:(user)=>(
                    <span>
                        <LinkButton>修改</LinkButton>
                        <LinkButton>删除</LinkButton>
                    </span>
                )
            }
        ]
    }
    componentWillMount(){
        //调用初始化列的方法
        this.initColumns()
        //调用所有用户的列表
        this.getUsers()
    }
    handleOk=()=>{
        this.setState({isShow:false})
    }
    render() {
        const title=(
            <Button type='primary' onClick={()=>{this.setState({isShow:true})}}>创建用户</Button>
        )
        const {isShow,users}=this.state
        return (
           <Card title={title}>
               <Table
                bordered
                rowKey=''
                dataSource={users}
                columns={this.columns}
                pagination={{defaultPageSize:5,showQuickJumper:true}}
               >
               </Table>
               <Modal
                title="添加用户"
                visible={isShow}
                onOk={this.handleOk}
                onCancel={()=>{this.setState({isShow:false})}}
               >
               </Modal>
           </Card>
        )
    }
}
