import React, { Component } from 'react'
import { Card, Table, Button, Icon } from 'antd';
export default class Category extends Component {
    render() {
        //card的左侧
        const title = '一级分类列表'
        //card的右侧
        const extra = (
            <Button type="primary">
                <Icon type='plus'></Icon>
                添加
            </Button>
        )
        const dataSource = [
            {
                "parentId": "0",
                "_id": "5e12b8bce31bb727e4b0e348",
                "name": "家用电器",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e130e60e31bb727e4b0e34b",
                "name": "手机",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e130ec7e31bb727e4b0e34c",
                "name": "洗衣机",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e1346533ed02518b4db0cd7",
                "name": "图书",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e13467e3ed02518b4db0cd8",
                "name": "杯具",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e1346c83ed02518b4db0cd9",
                "name": "纸",
                "__v": 0
            },
            {
                "parentId": "0",
                "_id": "5e144dc7297c1138787e96ab",
                "name": "服装",
                "__v": 0
            },
        ];

        const columns = [
            {
                title: '分类的名称',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '年龄',
                dataIndex: 'age',
                key: 'age',
            },
            {
                title: '住址',
                dataIndex: 'address',
                key: 'address',
            },
        ]

        return (
            <div>
                <Card title={title} extra={extra} >
                    <Table dataSource={dataSource} columns={columns} />
                </Card>
            </div>
        )
    }
}
