     //根据menu数组生成对应的标签数组   reduce+递归调用
    getMenuNodes=((menuList)=>{
        return menuList.reduce(((pre,item)=>{
            //向pre 添加<Menu.Item/>
            //向pre 添加<SubMneu />
            if(!Item.children){
                pre.push(( <Menu.Item key={item.key}>
                    <Link to={item.key}>
                    <Icon type={item.icon} />
                    <span>{item.title}</span>
                    </Link>
                </Menu.Item>))
            }else{
                pre.push((
                    <SubMenu
                    key={item.key}
                    title={
                      <span>
                        <Icon type={item.icon}/>
                        <span>{item.title}</span>
                      </span>
                    }
                  >
                  {
                      this.getMenuNodes(item.children)
                  }
                  </SubMenu> 
                ))
            }
                return pre
        }),[])
    })
    render() {
        return (
           <div>
             <header className="left-nav">
                <Link  to='/' className='left-nav-header'>
                    <img alt='logo' src={logo}></img>
                    <h1>商品后台</h1>
                </Link>
            </header>

   
            <Menu
          defaultSelectedKeys={['1']}
          defaultOpenKeys={['']}  //默认选中那个菜单项
          mode="inline"
          theme="dark"
        >

            {
                this.getMenuNodes(menuList)
            }
          {/* <Menu.Item>
            <Link to='/user'>
            <Icon type="mail" />
             <span>用户管理</span>
            </Link>
          </Menu.Item>

          <SubMenu
            key="sub4"
            title={
              <span>
                <Icon type="mail" />
                <span>图形图表</span>
              </span>
            }
          >
            <Menu.Item key="8">
                <Link to='/charts/bar'>
                    <Icon type="mail" />
                    <span>柱形图</span>
                 </Link>
            </Menu.Item>

            <Menu.Item key="9">
                <Link to='/charts/line'>
                    <Icon type="mail" />
                    <span>折线图</span>
