import React, { Component } from 'react'
import {Card,Select,Input,Button,Icon,Table} from 'antd'
import {getProducts} from '../../api/index'
//Product的默认子路由组件
const Option=Select.Option
export default class ProductHome extends Component {
    state={
        products:[], //商品的数组
    }
    //初始化列的方法
    initColumns=()=>{
        return this.columns=[
            {
              title: '姓名',
              dataIndex: 'name',
              key: 'name',
            },
            {
              title: '年龄',
              dataIndex: 'age',
              key: 'age',
            },
            {
              title: '住址',
              dataIndex: 'address',
              key: 'address',
            },
          ];
    }
    //获取商品的方法
    getProducts=async()=>{
        const pageNum=3
        const pageSize=5
       const result= await getProducts({pageNum,pageSize})
      this.columns=result.data.list
    }
    componentWillMount(){
        this.initColumns()  //调用初始化列的方法
    }
    componentDidMount(){
        this.getProducts()   //调用获取商品的方法
    }
    render() {
        //取出状态数据
        const {products}=this.state
       
        const title=(
            <span>
                <Select value='1' style={{width:150}}>
                    <Option value='1'>按名称搜索</Option>
                    <Option value='2'>按描述搜索</Option>
                </Select>
                <Input placeholder='关键字' style={{width:150,margin:'0 15px'}}></Input>
                <Button type='primary'>搜索</Button>
            </span>
        )
        const extra=(
           <Button type='primary'>
                <Icon type='plus' ></Icon>   添加商品
           </Button>
        )
        return (
          <Card title={title} extra={extra}>
              <Table dataSource={products} columns={this.columns}></Table>
          </Card>
        )
    }
}
