import React, { Component } from 'react'
import { Card,Table,Button,Icon } from 'antd';
export default class Category extends Component {
    render() {
        //card的左侧
        const title='一级分类列表'
        //card的右侧
        const extra=(
            <Button type="primary">
                <Icon type='plus'></Icon>
                添加
            </Button>
        )
        return (
            <div>
                <Card title={title} extra={extra} >
                  <Table></Table>
                </Card>
            </div>
        )
    }
}
