import React, { Component } from 'react'
import{
    Card,Button,Table,Modal
} from 'antd'
import formateDate from '../../utils/dateUtils'
import {reqUsers} from '../../api/index'
export default class User extends Component {
    state={
        status:false,  //对话框的显示隐藏
        users:[],  //所有用户的数组
    }
    //获取所有用户的方法
    getUsers=async()=>{
        const result=await reqUsers()
        console.log(result)
    }
    //初始化列的方法
    initColumns=()=>{
        this.columns=[
            {
                title:'用户名',
                dataIndex:'username',
            },
            {
                title:'邮箱',
                dataIndex:'email'
            },
            {
                title:'电话',
                dataIndex:'phone'
            },
            {
                title:'注册时间',
                dataIndex:'create_time',
                render:formateDate
            },
            {
                title:'所属角色',
                dataIndex:'',
            },
            {
                title:'操作',
                dataIndex:'',

            }
        ]
    }
    componentWillMount(){
        //调用初始化列的方法
        this.initColumns()
        //调用所有用户的列表
        this.getUsers()
    }
    handleOk=()=>{
        this.setState({status:false})
    }
    render() {
        const title=(
            <Button type='primary' onClick={()=>{this.setState({status:true})}}>创建用户</Button>
        )
        const {status,users}=this.state
        return (
           <Card title={title}>
               <Table
                bordered
                rowKey=''
                dataSource={users}
                columns={this.columns}
                pagination={{defaultPageSize:5,showQuickJumper:true}}
               >

               </Table>
               <Modal
                title="创建角色"
                visible={status}
                onOk={this.handleOk}
                onCancel={()=>{this.setState({status:false})}}
               >

               </Modal>
           </Card>
        )
    }
}
